import React,{ useState } from 'react'
import MapGL, {GeolocateControl } from 'react-map-gl'
import 'mapbox-gl/dist/mapbox-gl.css'

const TOKEN='pk.eyJ1Ijoidi1wYXNoYWxpdWsiLCJhIjoiY2szaGZtMzF1MGM0aDNjbXN3emd2bm9qaiJ9.FDxxP93p6jNROtLNi3TgcQ'

const geolocateStyle = {
  float: 'left',
  marginTop: '150px',
  marginLeft: '20px',
  padding: '10px'
};

const Map = () => {

var monument = [-77.0353, 38.8895];

  const [viewport, setViewPort ] = useState({
    width: 1380,
    height: 700,
    latitude: 40.75,
    longitude: -74.0060, //NYC Coordinates 
    center: monument,
    zoom: 10
  })

  const _onViewportChange = viewport => setViewPort({...viewport, transitionDuration: 10})

  return (
    <div style={{ margin: "auto", width: "1380px", height: "400px", overflow: "hidden", borderRadius: "25px"}}>
      <MapGL
        {...viewport}
        mapboxApiAccessToken={TOKEN}
        maxZoom={12.5}
        mapStyle="mapbox://styles/v-pashaliuk/ck3m12hat31my1dol309wge8p"
        onViewportChange={_onViewportChange}
        style={{marginTop: "-150px"}}
      >
        <Marker latitude={40.75} longitude={-74} offsetLeft={-20} offsetTop={-10}></Marker>
        <GeolocateControl
          style={geolocateStyle}
          positionOptions={{enableHighAccuracy: true}}
          trackUserLocation={true}
        />
      </MapGL>
    </div>
  )
}

export default Map
