import React, {Fragment} from "react";
import { Redirect } from 'react-router-dom'
import { connect } from 'react-redux'
import { setUser, updatePortfolio } from './redux/actions/user_actions'

import Noty from 'noty';  
import "../node_modules/noty/lib/noty.css";  
import "../node_modules/noty/lib/themes/mint.css";

class SellComponent extends React.Component{
    state = {
        availableStocks: 0
    }

    componentDidMount(){
        this.setState({
            availableStocks: this.props.stock.quantity
        })
    }

    render(){
        console.log(this.props)
        return (
        <div className="sell_card">
            <p className="form_name" align="center" >Do you really wat to sell this stock?</p>
            <p align="center">
            The stork you chose to sell is: {this.props.stock.companyName}({this.props.stock.ticker})<br />
            Current price for this stock is: ${this.props.stock.current_price}<br />
            You have {this.props.stock.quantity} available.
            </p>
            <div>
                <button>+</button>

                <button>-</button>
            </div>
        </div>
        );
    }
}

function msp(state){
    return {
      currentUser: state.currentUser
    }
}

export default connect(msp, { setUser })(SellComponent)
