import React from "react";
import Card from './Card'

export default class CardsContainer extends React.Component{
    state = {
        events: []
    }

    componentDidMount(){
        fetch(`http://localhost:4000/Events`)
        .then(resp => resp.json())
        .then(fetchedEvents => this.setState({events: fetchedEvents}))
        .then(this.setState({loaded: true}))
    } 

    render(){
        return (
            <div>
                {/* <h1>Some text</h1> */}
                {this.props.events.map(event => <Card event={event}/>)}
                <a>More info</a>
            </div>
        );
    }
}
