import React from "react";

export default class LogIn extends React.Component{
    render(){
        state = {
            username: "",
            password: ""
          }

          handleChange = (event) => {
            this.setState({
              [event.target.name]: event.target.value
            })
          }

          handleSubmit = (e) => {
            e.preventDefault()

            fetch("http://localhost:3001/api/v1/login", {
              method: "POST",
              headers: {
                "Content-Type": "application/json",
                "Accept": "application/json"
              },
              body: JSON.stringify(this.state)
            })
            .then(res => res.json())
            .then(response => {
              //set user to state
              //redirect!
              if (response.errors){
                alert(response.errors)
              } else {
                this.props.setUser(response)
              }
            })

        }

        return (
        <div className="login-main">
            <p className="sign" align="center" >Log In</p>
            <form className="form1">
                <input className="un" type="text" align="center"  placeholder="Username" />
                <input className="pass" type="password" align="center"  placeholder="Password" />
                <a className="submit" align="center" >Log In</a>
            </form>
        </div>
        );
    }
}
