import React from 'react';
import FactoryReactMapboxGl from 'react-mapbox-gl';
import { GeolocateControl } from "mapbox-gl";
import IntroVid from './IntroVid'


const Map = FactoryReactMapboxGl({
    accessToken: 'pk.eyJ1Ijoidi1wYXNoYWxpdWsiLCJhIjoiY2szaGZtMzF1MGM0aDNjbXN3emd2bm9qaiJ9.FDxxP93p6jNROtLNi3TgcQ',
  })

class MainBody extends React.Component {
    state = {
        lng: 5,
        lat: 34,
        zoom: 2
    };

    onMapLoad = (map) => { map.addControl(new GeolocateControl()); }

    render() {
        return (
            <div>
                <IntroVid />
                <p>nearest map?</p>
                <Map
                    onStyleLoad={this.onMapLoad}
                    style={{ position: "absolute", top: "0", bottom: "0", width: "100%" }}
                />
                <div ref={el => this.mapContainer = el} className="mapContainer" />
                <p>nearest carousel</p>
                <p>top picks</p>
                <p>picks by neighbourhood</p>
            </div>

        );
    }
}

export default MainBody;
