import React, {Fragment} from "react";
import { Redirect } from "react-router-dom"
import { connect } from 'react-redux'
import { setUser } from './redux/actions/user_actions'

class Portfolio extends React.Component{
    state = {
        ticker: "",
        quantity: null
    }

    handleChange = (event) => {
        this.setState({
          [event.target.name]: event.target.value
        })
    }

    componentDidMount(){

    }

    render(){
        return (
            <Fragment>
                {this.props.currentUser?
                    <Fragment>
                        <div className="portfolio_main">
                            <div className="stocks_info">

                            </div>
                            <div className="buy_info">
                                <form className="buy_form" onSubmit={this.handleSubmit}>
                                <p className="form_name" align="center" >Cash - ${this.props.currentUser.balance}</p>
                                    <input 
                                        className="input"
                                        style={{margin: "10px auto"}}
                                        type="text" 
                                        name="ticker" 
                                        value={this.state.ticker} 
                                        onChange={this.handleChange} 
                                        placeholder="Ticker" 
                                    />
                                    <input 
                                        className="input"
                                        style={{margin: "10px auto"}} 
                                        type="text" 
                                        pattern= "^[0-9]"
                                        name="quantity" 
                                        value={this.state.quantity} 
                                        onChange={this.handleChange} 
                                        placeholder="Qty." 
                                    />
                                    <input 
                                        className="submit_button" 
                                        style={{margin: "10%"}}
                                        type="submit" 
                                        value="Buy" 
                                    />
                                </form>
                            </div>
                        </div>
                    </Fragment>
                    :
                    <Fragment>
                        <h1 align="center">Loading...</h1>
                    </Fragment>

                }
            </Fragment>
        );
    }
}

function msp(state){
    return {
      currentUser: state.currentUser
    }
  }

  export default connect(msp, {setUser})(Portfolio)
