import React from "react";
import NavBar from './NavBar'
import '../src/App.css'


export default class App extends React.Component {
  state = {
    events: [],
    loaded: false
}

  componentDidMount(){
    fetch(`http://localhost:4000/Events`)
    .then(resp => resp.json())
    .then(fetchedEvents => this.setState({events: fetchedEvents}))
    .then(this.setState({loaded: true}))
  } 

  render(){
    return (
      <div>
        {this.state.loaded ?
        <NavBar events={this.state.events} />
        :
        <h1>Loading,fucker...</h1>
        }
      </div>
    );
  }
}
