import React, {Fragment} from "react";
import { Redirect } from "react-router-dom"
import { connect } from 'react-redux'
import { setUser } from './redux/actions/user_actions'

class Portfolio extends React.Component{
    state = {
        ticker: "",
        quantity: 0
    }

    handleChange = (event) => {
        this.setState({
          [event.target.name]: event.target.value
        })
    }

    handleSubmit = (e) => {
        e.preventDefault()
        fetch("http://localhost:3000/api/v1/search", {
        method: "POST",
        headers: {
            "Content-Type": "application/json",
            "Accept": "application/json"
        },
        body: JSON.stringify({...this.state, user_balance: this.props.currentUser.balance})
        })
        .then(res => res.json())
        .then(response => {
            if(response.error){
                alert(response.error)
            } else {
                console.log(response)
            }
        })
    }

    componentDidMount(){

    }

    render(){
        return (
            <Fragment>
                {this.props.currentUser?
                    <Fragment>
                        <div className="portfolio_main">
                            <div className="stocks_info">

                            </div>
                            <div className="buy_info">
                                <form className="buy_form" onSubmit={this.handleSubmit}>
                                <p className="form_name" align="center" >Cash - ${this.props.currentUser.balance}</p>
                                    <input 
                                        className="input"
                                        style={{margin: "10px auto"}}
                                        type="text" 
                                        name="ticker" 
                                        value={this.state.ticker} 
                                        onChange={this.handleChange} 
                                        placeholder="Ticker" 
                                    />
                                    <input 
                                        className="input"
                                        style={{margin: "10px auto"}} 
                                        type="text"
                                        name="quantity" 
                                        value={this.state.quantity} 
                                        onChange={this.handleChange} 
                                        placeholder="Qty." 
                                    />
                                    <input 
                                        className="submit_button" 
                                        style={{margin: "10%"}}
                                        type="submit" 
                                        value="Buy" 
                                    />
                                </form>
                            </div>
                        </div>
                    </Fragment>
                    :
                    <Fragment>
                        <h1 align="center">Loading...</h1>
                    </Fragment>

                }
            </Fragment>
        );
    }
}

function msp(state){
    return {
      currentUser: state.currentUser
    }
  }

  export default connect(msp, {setUser})(Portfolio)
