import React, { Fragment } from "react";
import {
  BrowserRouter as Router,
  Route,
  Redirect
} from "react-router-dom";
import '../src/App.css'
import NavBar from './NavBar'
import Home from './Home'
import LogIn from './LogIn'
import SignUp from './SignUp'
import LogOut from './LogOut'
import Profile from './Profile'
import CardInfo from './CardInfo'
import EventsContainer from './EventsContainer'


export default class App extends React.Component {
  // state = {
  //   currentUser: null,

  //   loaded: false,

  //   showEvent: {},

  //   allNearestEvents: [],
  //   allChoosenAreaEvents: [],
  //   allFreeEvents: [],
  //   allRandEvents: [],
  //   showEvents: [],

  //   myEvents: [],
  //   myLat: 0,
  //   myLong: 0
  // }
  state = {
    loaded:false
  }
  componentDidMount(){
    const token = localStorage.token
    this.setState({
      loaded: true
    })
    if(token){
      fetch("http://localhost:3000/api/v1/auto_login", {
        headers: {
          "authorization": token
        }
      })
      .then(res => res.json())
      .then(response =>{
        if (response.errors){
          alert(response.errors)
        } else {
          this.setState({
            currentUser: response
          })
        }
      })
    }
  }

  // logout = () => {
  //   this.setState({
  //     currentUser: null
  //   }, () => {
  //     localStorage.removeItem("token")
  //   })
  // }


  render() {
    return (
      <Fragment>
        {this.state.loaded?
          <Router>
            <div>
              <NavBar />
              <Route path="/logout">
                  <LogOut logout={props.logout}/>
              </Route>
              <Route path="/login">
                  <LogIn setUser={props.setUser} currentUser={props.currentUser} />
              </Route>
              <Route path="/signup">
                  <SignUp setUser={props.setUser} currentUser={props.currentUser} />
              </Route>
              <Route path="/profile">
                  {props.currentUser !== null ?
                      <Profile 
                          currentUser={props.currentUser} 
                          setUser={props.setUser} 
                          logout={props.logout} 
                          setMyEvents={props.setMyEvents}
                          myEvents={props.myEvents}
                          chooseEvent={props.chooseEvent}
                      />
                      :
                      <Redirect to="/" />
                  }
              </Route>
              <Route path="/events">
                  <EventsContainer 
                      showEvents={props.showEvents}
                      chooseEvent={props.chooseEvent}
                  />
              </Route>
              <Route path="/event">
                  {
                      Object.keys(props.chosenEvent).length !== 0 ?
                          <CardInfo 
                              currentUser={props.currentUser} 
                              chosenEvent={props.chosenEvent}
                              myEvents={props.myEvents}
                              removeEvent={props.removeEvent}
                              addEvent={props.addEvent}
                              myLat={props.myLat}
                              myLong={props.myLong}
                              />
                              :
                          <Redirect to="/" />
                  }
              </Route>
              <Route path="/">
                  <Home 
                      chooseEvent={props.chooseEvent}
                      setAllNearesEvents={props.setAllNearesEvents}
                      setAllChoosenEvents={props.setAllChoosenEvents}
                      setAllFreeEvents={props.setAllFreeEvents}
                      setAllRandEvents={props.setAllRandEvents}
                      setMyCoords={props.setMyCoords}
                      setContainerId={props.setContainerId}
                  />
              </Route>
            </div>
          </Router>
          :
          <div className="lds-spinner"><div></div><div></div><div></div>
          <div></div><div></div><div></div><div></div><div></div>
          <div></div><div></div><div></div><div></div></div>
        }
      </Fragment>
    );
  }
}
