import React from "react";

export default class SignUp extends React.Component{
    state = {
        username: "",
        password: "",
        passwordConfirmation: ""
    }

    handleChange = (event) => {
        this.setState({
          [event.target.name]: event.target.value
        })
      }

      handleSubmit = (e) => {
        e.preventDefault()

        if (this.state.password === this.state.passwordConfirmation){
          fetch("http://localhost:3001/api/v1/signup", {
            method: "POST",
            headers: {
              "Content-Type": "application/json",
              "Accept": "application/json"
            },
            body: JSON.stringify({username: this.state.username, password: this.state.password})
          })
          .then(res => res.json())
          .then(response => {
            if(response.errors){
              alert(response.errors)
            } else {
              // send them somewhere
              // storing the user object SOMEWHERE
              this.props.setUser(response)
            }
          })
        } else {
          alert("Passwords don't match!")
        }

      }


    render(){
        return (
            <div className="signup-main">
            <p className="sign" align="center" >Sign Up</p>
            <form className="form1" onSubmit={this.handleSubmit}>
                <input className="un" type="text" align="center"  placeholder="First Name" />
                <input className="un" type="text" align="center"  placeholder="Last Name" />
                <input className="un" type="text" align="center"  placeholder="Phone Number" />
                <input className="un" type="text" align="center"  placeholder="Username" />
                <input className="pass" type="password" align="center"  placeholder="Password" />
                <input className="pass" type="password" align="center"  placeholder="Password Confirmation" />
                <a className="submit" align="center" >Sign Up</a>
            </form>
        </div>
        );
    }
}
