import React from "react";
import { Redirect } from 'react-router-dom'

export default class SignUp extends React.Component{
    state = {
        name: "",
        phonenumber: "",
        email: "",
        password: "",
        passwordConfirmation: ""
    }

    handleChange = (event) => {
        if(event.target.name === "password"){
            this.setState({
                password: event.target.value,
                passwordConfirmation: event.target.value
            })
        }else{
            this.setState({
                [event.target.name]: event.target.value
            })
        }
      }

    handleSubmit = (e) => {
        e.preventDefault()
        if (this.state.password === this.state.passwordConfirmation){
            fetch("http://localhost:3000/api/v1/signup", {
            method: "POST",
            headers: {
                "Content-Type": "application/json",
                "Accept": "application/json"
            },
            body: JSON.stringify(this.state)
            })
            .then(res => res.json())
            .then(response => {
            if(response.errors){
                alert(response.errors)
            } else {
                this.props.setUser(response)
                this.setState({
                    name: "",
                    phonenumber: "",
                    email: "",
                    password: "",
                    passwordConfirmation: ""
                })
            }
            })
        } else {
            alert("Passwords don't match!")
        }

    }


    render(){
        if (this.props.currentUser !== null && this.props.currentUser !== undefined) {
            return <Redirect to='/' />;
        } 
        return (
            <div className="signup-main">
            <p className="sign" align="center" >Sign Up</p>
            <form className="form1" onSubmit={this.handleSubmit}>
                <input className="un" type="text" align="center" name="name" value={this.state.name} onChange={this.handleChange} placeholder="First Name" />
                <input className="un" type="text" align="center" name="email" value={this.state.email} onChange={this.handleChange} placeholder="Email" />
                <input className="pass" type="password" align="center" name="password" value={this.state.password} onChange={this.handleChange} placeholder="Password" />
                <input className="submit" align="center" type="submit" value="Sign Up" />
            </form>
        </div>
        );
    }
}
