import React from 'react';
import FactoryReactMapboxGl from 'react-mapbox-gl';
import mapboxgl,{ GeolocateControl } from "mapbox-gl";
import IntroVid from './IntroVid'
import Map from './Map'
import CardsContainer from './CardsContainer'

class MainBody extends React.Component {
    state = {
        current_lat: 40.75,
        current_long: -74.0060
    }

    setLatLong = (lat, long) => {
        this.setState({
            current_lat: lat,
            current_long: long
        })
    }

    render(){
        return (
            <div>
                <IntroVid />
                <p>nearest map?</p>
                <Map setLatLong={this.setLatLong} />
                {/* <div ref={el => this.mapContainer = el} className="mapContainer" /> */}
                <p>nearest carousel</p>
                <CardsContainer lat={this.state.current_lat} long={this.state.current_long}/>
                <p>top picks</p>
                <p>picks by neighbourhood</p>
            </div>

        );
    }
}

export default MainBody;
