import React from "react";
import Card from './Card'

export default class CardsContainer extends React.Component{
    render(){
        return (
            <div>   
                {this.state.loaded ?
                <div className="cards">
                    {this.state.nearestEvents !== undefined ? this.state.nearestEvents.map(event => <Card key={event.Name} event={event} showEventInfo={this.props.showEventInfo} />) : null}
                    {
                        this.state.nearestEvents.length > 0 ?
                            <p>Show me more!</p>
                        :
                            <p style={{paddingLeft: "40px"}}>No events to show, sorry!</p>
                    }
                </div>
                :
                <div>
                    <h1>Loading...</h1>
                </div>


                }
            </div>
        );
    }
}
