import React, {Fragment} from "react";
import { Redirect } from "react-router-dom"
import { connect } from 'react-redux'
import { setUser } from './redux/actions/user_actions'

import Noty from 'noty';  
import "../node_modules/noty/lib/noty.css";  
import "../node_modules/noty/lib/themes/mint.css"; 

class Portfolio extends React.Component{
    state = {
        ticker: "",
        quantity: ""
    }

    handleChange = (event) => {
        this.setState({
          [event.target.name]: event.target.value
        })
    }

    handleSubmit = (e) => {
        e.preventDefault()
        fetch("http://localhost:3000/api/v1/search", {
        method: "POST",
        headers: {
            "Content-Type": "application/json",
            "Accept": "application/json"
        },
        body: JSON.stringify({...this.state, user_balance: this.props.currentUser.balance})
        })
        .then(res => res.json())
        .then(response => {
            if(response.error){
                new Noty({  
                    text: `${response.error}`,
                    layout: "bottomRight",
                    type: "alert",
                    timeout: 1000
                }).show()
            } else {
                let notif = new Noty({  
                    text: "Some notification text",
                    layout: "bottomRight",
                    buttons: [
                        Noty.button('YES', 'btn btn-success', function () {
                            console.log('button 1 clicked');
                        }),

                        Noty.button('NO', 'btn btn-error', function () {
                            console.log('button 2 clicked');
                            notif.close();
                        })
                      ]
                  }).show()
            }
        })
    }

    componentDidMount(){

    }

    render(){
        return (
            <Fragment>
                {this.props.currentUser?
                    <Fragment>
                        <div className="portfolio_main">
                            <div className="stocks_info">

                            </div>
                            <div className="buy_info">
                                <form className="buy_form" onSubmit={this.handleSubmit}>
                                <p className="form_name" align="center" >Cash - ${this.props.currentUser.balance}</p>
                                    <input 
                                        className="input"
                                        style={{margin: "10px auto"}}
                                        type="text" 
                                        name="ticker" 
                                        value={this.state.ticker} 
                                        onChange={this.handleChange} 
                                        placeholder="Ticker" 
                                    />
                                    <input 
                                        className="input"
                                        style={{margin: "10px auto"}} 
                                        type="text"
                                        name="quantity" 
                                        value={this.state.quantity} 
                                        onChange={this.handleChange} 
                                        placeholder="Qty." 
                                    />
                                    <input 
                                        className="submit_button" 
                                        style={{margin: "10%"}}
                                        type="submit" 
                                        value="Buy" 
                                    />
                                </form>
                            </div>
                        </div>
                    </Fragment>
                    :
                    <Fragment>
                        <h1 align="center">Loading...</h1>
                    </Fragment>

                }
            </Fragment>
        );
    }
}

function msp(state){
    return {
      currentUser: state.currentUser
    }
  }

  export default connect(msp, {setUser})(Portfolio)
