import React, { Fragment } from "react";
import {
  BrowserRouter as Router,
  Route,
  Redirect
} from "react-router-dom";
import '../src/App.css'
import NavBar from './NavBar'
import Home from './Home'
import LogIn from './LogIn'
import SignUp from './SignUp'
import LogOut from './LogOut'
import Profile from './Profile'
import CardInfo from './CardInfo'
import EventsContainer from './EventsContainer'


export default class App extends React.Component {
  // state = {
  //   currentUser: null,

  //   loaded: false,

  //   showEvent: {},

  //   allNearestEvents: [],
  //   allChoosenAreaEvents: [],
  //   allFreeEvents: [],
  //   allRandEvents: [],
  //   showEvents: [],

  //   myEvents: [],
  //   myLat: 0,
  //   myLong: 0
  // }
  state = {
    loaded:false
  }
  componentDidMount(){
    const token = localStorage.token
    this.setState({
      loaded: true
    })
    if(token){
      fetch("http://localhost:3000/api/v1/auto_login", {
        headers: {
          "authorization": token
        }
      })
      .then(res => res.json())
      .then(response =>{
        if (response.errors){
          alert(response.errors)
        } else {
          this.setState({
            currentUser: response
          })
        }
      })
    }
  }

  setUser = (response) => {
    this.setState({
      currentUser: response.user
    }, () => {
      localStorage.token = response.token
    })
  }

  logout = () => {
    this.setState({
      currentUser: null
    }, () => {
      localStorage.removeItem("token")
    })
  }

  chooseEvent= event =>{
    this.setState({
      showEvent: event
    })
  }

  setContainerId = id => {
    switch (id) {
      case 0:
          this.setState({
            showEvents: this.state.allChoosenAreaEvents
          })
        break;
      case 1:
          this.setState({
            showEvents: this.state.allNearestEvents
          })
        break;
      case 2:
          this.setState({
            showEvents: this.state.allFreeEvents
          })
        break;
      case 3:
        this.setState({
          showEvents: this.state.allRandEvents
        })
      break;
      default:
        break;
    }

  }

  setAllNearesEvents= events =>{
    this.setState({
      allNearestEvents: events
    })
  }

  setAllChoosenEvents= events =>{
    this.setState({
      allChoosenAreaEvents: events
    })
  }

  setAllFreeEvents= events =>{
    this.setState({
      allFreeEvents: events
    })
  }

  setAllRandEvents= events =>{
    this.setState({
      allRandEvents: events
    })
  }

  setMyCoords= (lat, long) => {
    this.setState({
      myLat: lat,
      myLong: long
    })
  }

  setMyEvents= events => {
    this.setState({
      myEvents: events
    })
  }

  removeEvent = event_name => {
    this.setState({
      myEvents: this.state.myEvents.filter(event => event.name !== event_name)
    })
  }

  addEvent= event => {
    this.setState({
      myEvents: [...this.state.myEvents, event]
    })
  }

  render() {
    return (
      <Fragment>
        {this.state.loaded?
          <Router>
            <div>
              <NavBar />
              <Route path="/logout">
                  <LogOut />
              </Route>
              <Route path="/login">
                  <LogIn />
              </Route>
              <Route path="/signup">
                  <SignUp />
              </Route>
              <Route path="/dashboard">
                  <Dashboard />
              </Route>
              <Route path="/groups">
                  <GroupsContainer add={0}/>
              </Route>
              <Route path="/add_group">
                  <GroupsContainer add={1}/>
              </Route>
              <Route path="/group/:id">
                  <GroupInfo/>
              </Route>
              <Route path="/create_group">
                  <GroupSignUp/>
              </Route>
              <Route path="/create_event">
                  <EventForm/>
              </Route>
              <Route path="/create_team">
                  <TeamSignUp />
              </Route>
              <Route path="/search_team">
                  <TeamSearch />
              </Route>
              <Route exact path="/profile">
                <Redirect to="/" />
              </Route>
              <Route exact path="/profile/@:username">
                  {this.props.currentUser !== null ?
                      <Profile 
                      />
                      :
                      <Redirect to="/" />
                    }
              </Route>
              <Route exact path="/">
              {this.props.currentUser === null ?
                      <InfoContainer />
                      :
                      <Redirect to="/dashboard" />
              }
              </Route>
            </div>
          </Router>
          :
          <div className="lds-spinner"><div></div><div></div><div></div>
          <div></div><div></div><div></div><div></div><div></div>
          <div></div><div></div><div></div><div></div></div>
        }
      </Fragment>
    );
  }
}
